			app.ApplicationServices.ConfigureComponents();

			app.UseMiddleware<ErrorHandlingMiddleware>();
			app.UseValidation();
			app.UseMiddleware<SaveChangesMiddleware>();
			
			app.UseMvc();
			
			PopulateDb(app.ApplicationServices);
		}

